import { AfterViewInit, ChangeDetectorRef, Component, OnInit, ViewChild } from '@angular/core';
import { MatSidenav } from '@angular/material/sidenav';
import {BreakpointObserver} from '@angular/cdk/layout'
import { NewsService } from './service/news.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements AfterViewInit, OnInit{
  title = 'NewsApp';
  articles:any = [];
  sources:any = [];
  selectedNews = "Latest News..."
  @ViewChild(MatSidenav) sideNav! : MatSidenav;

  constructor(private observer: BreakpointObserver, private cdr : ChangeDetectorRef, private newsApi:NewsService){
  }

  ngOnInit(): void {
    this.newsApi.initArticles().subscribe((res:any)=>{
      console.log(res.articles);
      this.articles = res.articles;
    });
    this.newsApi.initSources().subscribe((res:any)=>{
      console.log(res.sources);
      this.sources = res.sources;
    })
  }

  ngAfterViewInit(): void {
    this.sideNav.opened = true;
    this.observer.observe(['(max-width:787px)']).subscribe(
      (res)=>{
        if(res?.matches){
          this.sideNav.mode = 'over';
          this.sideNav.close();
        }else{
          this.sideNav.mode = 'side';
          this.sideNav.open();
        }
      }
    );
    this.cdr.detectChanges();
  }

  getNews(source:any){
    this.newsApi.getArticlesByID(source.id).subscribe(
      (res:any)=>{
        this.articles = res.articles;
        this.selectedNews = source.name
      }
    )
  }
}
